mapper.select:
select a.*, b.category_nav from product a left join (
SELECT CONCAT(A1.group_ID, A1.CATEGORY_ID) AS CATEGORY_KEY, ##카테고리 키 (그룹 + 키 형태로 카테고리분류) CASE A1.category_parent_LV WHEN 0 THEN A1.category_detail_NM WHEN 1 THEN CONCAT(A2.category_detail_NM,' > ', A1.category_detail_NM) WHEN 2 THEN CONCAT(A3.category_detail_NM, ' > ', A2.category_detail_NM,' > ', A1.category_detail_NM) END as CATEGORY_NAV FROM category A1 LEFT OUTER JOIN category A2 ON A1.CATEGORY_PARENT_LV = A2.CATEGORY_LV AND A1.CATEGORY_DETAIL_PARENT_LV = A2.CATEGORY_DETAIL_LV AND A1.GROUP_ID = A2.GROUP_ID LEFT OUTER JOIN category A3 ON A2.CATEGORY_PARENT_LV = A3.CATEGORY_LV AND A2.CATEGORY_DETAIL_PARENT_LV = A3.CATEGORY_DETAIL_LV ORDER BY CATEGORY_NAV ASC
) b
on a.category_no = b.category_key where user_id = #{user_id} order by a.product_no desc limit #{cri.pageStart}, #{cri.amount}

select a.*, b.category_nav from product a left join (
SELECT CONCAT(A1.group_ID, A1.CATEGORY_ID) AS CATEGORY_KEY, ##카테고리 키 (그룹 + 키 형태로 카테고리분류) CASE A1.category_parent_LV WHEN 0 THEN A1.category_detail_NM WHEN 1 THEN CONCAT(A2.category_detail_NM,' > ', A1.category_detail_NM) WHEN 2 THEN CONCAT(A3.category_detail_NM, ' > ', A2.category_detail_NM,' > ', A1.category_detail_NM) END as CATEGORY_NAV FROM category A1 LEFT OUTER JOIN category A2 ON A1.CATEGORY_PARENT_LV = A2.CATEGORY_LV AND A1.CATEGORY_DETAIL_PARENT_LV = A2.CATEGORY_DETAIL_LV AND A1.GROUP_ID = A2.GROUP_ID LEFT OUTER JOIN category A3 ON A2.CATEGORY_PARENT_LV = A3.CATEGORY_LV AND A2.CATEGORY_DETAIL_PARENT_LV = A3.CATEGORY_DETAIL_LV ORDER BY CATEGORY_NAV ASC
) b
on a.category_no = b.category_key where user_id = #{user_id} and product_name like concat('%',#{product_name},'%') order by a.product_no desc limit #{cri.pageStart}, #{cri.amount}

mapper.update:
update product set product_name = #{product_name},
				   product_price = #{product_price},
				   product_stock = #{product_stock},
				   product_description = #{product_description}
				   where product_no = #{product_no}

